class PrimeFlip{
    int N = 1E7, n, m;
    vi mapping;
    vb vist;
    vector<vb> E;
    void init(){
        int K;
        cin>>K;
        unordered_set<int> cnt;
        vi odds,evens;
        for(int i=0;i<K;++i){
            int x;
            cin>>x;
            if(cnt.count(x)) cnt.erase(x);
            else cnt.insert(x);
            cnt.insert(x+1);
        }
        for(auto k:cnt){
            if(k%2) odds.push_back(k);
            else evens.push_back(k);
        }
        cnt.clear();
        n = (int)odds.size();
        m = (int)evens.size();
        mapping = vi(m,-1);
        vist = vb(m,false);
        E = vector<vb>(n,vb(m, false));
        vb prime(N+1,true);
        prime[0] = prime[1] = false;
        for(int i=2;i<=N;++i) if(prime[i]){
            for(int j=2;j*i<=N;++j) prime[i*j] = false;
        }
        prime[2] = false;
        for(int i=0;i<n;++i) for(int j=0;j<m;++j) E[i][j] =prime[abs(odds[i]-evens[j])];
        prime.clear();
    }
    bool arange(int i){
        for(int j=0;j<m;++j) if(E[i][j] && !vist[j]){
            vist[j] = true;
            if(mapping[j]==-1 || arange(mapping[j])){
                mapping[j] = i;
                return true;
            }
        }
        return false;
    }
public:
    void solve(){
        init();
        int cnt = 0;
        for(int i=0;i<n;++i) cnt += arange(i);
        int ans = cnt + ((n-cnt)/2 + (m-cnt)/2)*2 + ((m-cnt)%2)*3;
        cout<<ans<<endl;
    }
};



int main(){
    PrimeFlip().solve();
    return 0;
}

