bool Prime(int x)
{
	if (x <= 1) return false;
	if (x == 2) return true;
	if (x % 2 == 0) return false;
	for (int i = 3; i * i <= x; i += 2)
		if (x % i == 0) return false;
	return true;
}

int Get(int x)
{
	if (x == 0) return 0;
	if (Prime(x) && x != 2) return 1;
	if (x == 4) return 4;
	if (x % 2 == 0) return 2;
	return 3;
}

