        if(x==T||flow<=0) return flow;
        int rest=0;
        for(int i=hd[x];i;i=e[i].nxt){
            int j=e[i].to;int v=e[i].val;
            if(dep[j]==dep[x]+1&&v){
                int now=dfs(j,min(v,flow));
                e[i].val-=now;
                e[i^1].val+=now;
                flow-=now;rest+=now;
                if(flow<=0) break;
            }
        }if(!rest) dep[x]=-1;
        return rest;
    }
