void sieve() {
	for(int i = 2; i < 1<<25; i++) {
		if(!p[i]) p[i] = i;
		for(ll j = i*1ll*i; j < 1<<25; j += i) if(!p[j]) p[j] = i;
	}
}
map<int, int> meme;
int d(int x) {
	assert(x < 1<<25);
	if(meme.count(x)) return meme[x];
	if(p[x] == x && x > 2) return meme[x] = 1;
	int t = 2 + (x&1);
	for(int i = 3; x-i > 2; i++)
		if(p[i]==i&&p[x-i]==x-i) {t = 2; break;}
	return meme[x] = t;
}
