int n,vs[107],xs[207],xp=0;
struct edge{
	int to,nx,v;
}e[100007];
int ep=2,e0[207],h[207],q[207],S,T;
bool bfs(){
	for(int i=1;i<=T;++i)h[i]=0;
	int ql=0,qr=0;
	h[q[++qr]=S]=1;
	while(ql!=qr){
		int w=q[++ql];
		if(w==T)return 1;
		for(int i=e0[w];i;i=e[i].nx)if(e[i].v){
			int u=e[i].to;
			if(!h[u])h[q[++qr]=u]=h[w]+1;
		}
	}
	return 0;
}
int dfs(int w,int f){
	if(w==T)return f;
	int c,used=0;
	for(int i=e0[w];i;i=e[i].nx)if(e[i].v){
		int u=e[i].to;
		if(h[u]!=h[w]+1)continue;
		c=dfs(u,std::min(e[i].v,f-used));
		e[i].v-=c;
		e[i^1].v+=c;
		used+=c;
		if(f==used)return f;
	}
	h[w]=0;
	return used;
}
bool isp(int x){
	if(x<0)x=-x;
	if((~x&1)||x<3)return 0;
	for(int i=3;i*i<=x;i+=2)if(x%i==0)return 0;
	return 1;
}
void ae(int a,int b){
	e[ep]=(edge){b,e0[a],1};e0[a]=ep++;
	e[ep]=(edge){a,e0[b],0};e0[b]=ep++;
}
