int n, m, s, t, x[N], col[N];
int head[N], nxt[M], ver[M], edge[M], d[N], cur[N], cnt = 1;

inline void add(int u, int v, int w)
{
	nxt[++cnt] = head[u], ver[cnt] = v, edge[cnt] = w, head[u] = cnt;
	nxt[++cnt] = head[v], ver[cnt] = u, edge[cnt] = 0, head[v] = cnt;
}

inline bool bfs()
{
	memset(d, 0, sizeof d); memcpy(cur, head, sizeof head);
	std::queue<int> Q;
	Q.push(s); d[s] = 1;
	while (Q.empty() == false)
	{
		int x = Q.front(); Q.pop();
		for (int i = head[x]; i; i = nxt[i])
		{
			int y = ver[i];
			if (edge[i] && !d[y])
			{
				d[y] = d[x] + 1;
				if (y == t) return true;
				Q.push(y);
			}
		}
	}
	return false;
}

inline int dinic(int x, int flow)
{
	if (x == t) return flow;
	int rest = flow, k = 0;
	for (int &i = cur[x]; i; i = nxt[i])
		if (edge[i] && d[ver[i]] == d[x] + 1)
		{
			k = dinic(ver[i], std::min(rest, edge[i]));
			edge[i] -= k, edge[i ^ 1] += k, rest -= k;
			if (!k) d[ver[i]] = 0;
			if (!rest) break;
		}
	return flow - rest;
}

inline bool isPrime(int x)
{
	if (x == 0 || x == 1) return false;
	if (x == 2) return true;
	if (!(x & 1)) return false;
	for (int i = 3; i * i <= x; i += 2) if (x % i == 0) return false;
	return true;
}

