	int prime[N + 20],tag[N + 20],mn[N + 20],Cnt;	
	void init (){
		rep(i,2,N){
			if ( !tag[i] ) prime[++Cnt] = i;
			rep(j,1,Cnt){
				if ( prime[j] * i > N ) break;
				tag[i * prime[j]] = 1;
				mn[i * prime[j]] = prime[j];
				if ( i % prime[j] == 0 ) break;
			}
		}
	}
