inline void adde(int x,int y){
	e[++cnt].to = y;
	e[cnt].next = head[x];
	head[x] = cnt;
}

bool find(int x){
	fore(i,x){
		int to = e[i].to;
		if ( !vis[to] && dy[to] == dx[x] + 1 ){
			vis[to] = 1;
			if ( !my[to] || find(my[to]) ){
				mx[x] = to;
				my[to] = x;
				return 1;
			}
		}
	}
	return 0;
}
void match(){
	memset(mx,0,sizeof(mx));
	memset(my,0,sizeof(my));
	tot = 0;
	while ( 1 ){
		bool flag = 0;
		tt = hh = 0;
		rep(i,1,cnt1) if ( !mx[i] ) q[tt++] = i;
		while ( hh < tt ){
			int x = q[hh++];
			fore(i,x){
				int to = e[i].to;
				if ( !dy[to] ){
					dy[to] = dx[x] + 1;
					if ( my[to] ){
						dx[my[to]] = dy[to] + 1;
						q[tt++] = my[to]; //
					}
					else flag = 1;//
				}
			}
		}
		if ( !flag ) break;
		memset(vis,0,sizeof(vis));
		rep(i,1,cnt1) if ( !mx[i] && find(i) ) tot++;
	}
}


