int N, x, cnt, match[MAX_N * 2 + 9];//, V;
bool is_prime[MAX_M + 9], rev[MAX_M + 9], used[MAX_N * 2 + 9];
vector<int> primes, v1, v2, G[MAX_N * 2 + 9];

void sieve() {
	memset(is_prime, 1, sizeof(is_prime));
	is_prime[0] = is_prime[1] = 0;
	for (int i = 2; i <= MAX_M; i++) {
		if (is_prime[i]) primes.push_back(i);
		for (int j : primes) {
			if (1LL * i * j > MAX_M) break;
			is_prime[i * j] = false;
			if (i % j == 0) break;
		}
	}
}

bool dfs(int v) {
	for (int u : G[v]) {
		if (!used[u]) {
			used[u] = true;
			if (match[u] < 0 || dfs(match[u])) {
				match[u] = v;
				return true;
			}
		}
	}
	return false;
}

void bipartite_matching() {
	memset(match, -1, sizeof(match));
	for (int v = 0; v < v1.size(); v++) {
		memset(used, 0, sizeof(used));
		if (dfs(v)) cnt++;
	}
}

int main() {
	sieve();
	scanf("%d", &N); 
	for (int i = 1; i <= N; i++) scanf("%d", &x), rev[x] ^= true;
	for (int i = 1; i <= MAX_M + 1; i++) {
		if (rev[i] != rev[i - 1]) {
			if (i & 1) v1.push_back(i);
			else v2.push_back(i);
		}
	}
	for (int i = 0; i < v1.size(); i++) {
		for (int j = 0; j < v2.size(); j++) {
			int m = abs(v1[i] - v2[j]);
			if (is_prime[m]) G[i].push_back(j);
		}
	}
//	V = v1.size() + v2.size();
	bipartite_matching();
	printf("%d", cnt + (v1.size() - cnt >> 1 << 1) + (v2.size() - cnt >> 1 << 1) + ((v1.size() - cnt) & 1) * 3);
	return 0;
}

