		mp[x[i]+1]^=1;
	}
	vi odd, even;
	for(auto& p : mp) if(p.se) (p.fi&1?odd:even).pb(p.se);
	rep(i, odd.size()) rep(j, even.size()) {
		if(is_prime[abs(odd[i]-even[j])]) add_edge(1000+i, 2000+j, 1);
	}
	rep(i, odd.size()) add_edge(0, 1000+i, 1);
	rep(i, even.size()) add_edge(2000+i, 1, 1);
	int f = max_flow(0, 1);
	int ans = f + (((int)odd.size()-f)/2)*2 + (((int)even.size()-f)/2)*2;
	if(((int)odd.size()-f)&1) ans += 3;
	cout << ans << endl;
	return 0;
}

