inline bool chk(int n)
{
	if (n <= 1)
		return false; 
	for (int i = 2; i * i <= n; i++)
	{
		if (!(n % i))
			return false; 
	}
	return true; 
}
bool dfs(int u)
{
	for (int v = 0; v < nb; v++)
	{
		if (vis[v] || !w[u][v])
			continue; 
		vis[v] = true; 
		if (-1 == from[v] || dfs(from[v]))
		{
			from[v] = u; 
			return true; 
		}
	}
	return false; 
}
