#include <bits/stdc++.h>
using namespace std;

const int N = 233;
const int INF = 1e9;

struct Edge {
    int to, rev, cap;
};

int nv, sv, tv;
vector<Edge> g[N];
int dist[N], ptr[N];

void add_edge(int a, int b, int c) {
    g[a].push_back(Edge{b, int(g[b].size()), c});
    g[b].push_back(Edge{a, int(g[a].size()) - 1, 0});
    assert(max(a, b) < nv);
}

bool augment() {
    fill_n(dist, nv, -1);
    dist[sv] = 0;

    vector<int> que;
    que.push_back(sv);
    for (int i = 0; i < int(que.size()); i++) {
        int v = que[i];
        for (auto e: g[v]) {
            if (dist[e.to] != -1 || e.cap == 0) {
                continue;
            }
            dist[e.to] = dist[v] + 1;
            if (e.to == tv) {
                return true;
            }
            que.push_back(e.to);
        }
    }

    return false;
}

int dfs(int v, int r) {
    if (v == tv) {
        return r;
    }
    for (int& i = ptr[v]; i < int(g[v].size()); i++) {
        auto& e = g[v][i];
        if (dist[e.to] <= dist[v] || e.cap == 0) {
            continue;
        }
        int d = dfs(e.to, min(r, e.cap));
        if (d == 0) {
            continue;
        }
        e.cap -= d;
        g[e.to][e.rev].cap += d;
        return d;
    }
    return 0;
}

int exec() {
    int res = 0;
    while (augment()) {
        fill_n(ptr, nv, 0);
        while (true) {
            int d = dfs(sv, INF);
            if (d == 0) {
                break;
            }
            res += d;
        }
    }
    return res;
}

const int M = int(1e7 + 10);

