void precalc() {
    fill(isp + 2, isp + M, true);
    for (int i = 2; i * i < M; i++) {
        if (!isp[i]) {
            continue;
        }
        for (int j = i * i; j < M; j += i) {
            isp[j] = false;
        }
    }
}

int main() {
