    scanf("%d", &n);

    set<int> a;
    auto add = [&](int x) {
        auto it = a.find(x);
        if (it != a.end()) {
            a.erase(it);
        } else {
            a.insert(x);
        }
    };

    for (int i = 0; i < n; i++) {
        int x;
        scanf("%d", &x);
        add(x);
        add(x + 1);
    }

    vector<vector<int>> v(2);
    for (int x: a) {
        v[x % 2].push_back(x);
    }
    int v0s = v[0].size(), v1s = v[1].size();

    sv = 0;
