int primes[M], prime_cnt, not_prime[M];
void get_primes() {
  not_prime[1] = 1;
  for (int i = 2; i <= M-10; i++) {
    if (!not_prime[i]) {
      primes[prime_cnt++] = i;
    }
    for (int j = 0; j < prime_cnt && i * primes[j] <= M-10; j++) {
      not_prime[i*primes[j]] = 1;
      if (i%primes[j] == 0)
        break;
    }
  }
}

