  int k = 0;
  FOR (i, L) {
    memset(vis, 0, sizeof(vis));
    if (dfs(i))
      k++;
  }

  printf("%d\n", k + ((L-k)/2+(R-k)/2)*2 + ((L-k)%2)*3);
