int gi() {
	int w=0;bool q=1;char c=getchar();
	while ((c<'0'||c>'9') && c!='-') c=getchar();
	if (c=='-') q=0,c=getchar();
	while (c>='0'&&c <= '9') w=w*10+c-'0',c=getchar();
	return q? w:-w;
}

int n;
int match[N],pre[N],q[N],vis[N],cnt;
int x[N];vector<int>e[N];

inline bool bfs(int k) {
	queue<int>q;
	++cnt;q.push(k);
	while (!q.empty()) {
		k=q.front();q.pop();
		for (int t:e[k]) if (vis[t]!=cnt) {
				if (match[t]) {
					vis[t]=cnt;
					pre[match[t]]=k;
					q.push(match[t]);
				} else {
					int p;
					while (k) {
						p=match[k];
						match[k]=t,match[t]=k;
						t=p,k=pre[k];
					}
					return true;
				}
			}
	}
	return false;
}

int pri[M];
bool in[M];
inline void init(int n) {
	int i,j,len=0;
	for (i=2;i<=n;i++) {
		if (!pri[i]) pri[++len]=i,in[i]=i!=2;
		for (j=1;j<=len&&i*pri[j]<=n;j++) {
			pri[i*pri[j]]=1;
			if (!(i%pri[j])) break;
		}
	}
}
int main()
{
	n=gi();int i,j,odd=0,s=0,m=0;
	for (i=1;i<=n;i++) x[++m]=gi(),x[m+1]=x[m]+1,m++;
	sort(x+1,x+1+m);
	for (i=1,n=0;i<=m;i++) if (x[i]==x[i+1]) i++; else x[++n]=x[i];

	init(x[n]-x[1]);

	for (i=1;i<=n;i++)
		if (x[i]&1) {
			odd++;
			for (j=1;j<=n;j++)
				if (in[abs(x[j]-x[i])])
					e[i].push_back(j);
		}
	for (i=1;i<=n;i++) if (x[i]&1) s+=bfs(i);

	cout<<n-s+((s-odd)&1);
	return 0;
}

