//Appropriately Changed
using W = ll;
using edge = struct {int to, rev; W cap, flow;};
using G = vector<vector<edge>>;

//Appropriately Changed
void add_edge(G &graph, int from, int to, W cap) {
	graph[from].push_back({to, int(graph[to].size()) , cap , 0});
	graph[to].push_back({from, int(graph[from].size()) - 1, 0 , 0});
}

// Description: 
// TimeComplexity: $ \mathcal{O}(EV^2) $ but fast
// Verifyed: AOJ GRL_6_A

W dinic(G &graph, int s, int t) {
	const W inf = 1LL << 50;
	const int n = graph.size();
	vector<int> level(n), iter(n);

	auto bfs = [&](int s, int t) {
		fill(begin(level), end(level), -1);
		queue<int> q;
		level[s] = 0, q.push(s);

		while (!q.empty()) {
			int v = q.front(); q.pop();
			for (auto &e : graph[v]) {
				if (level[e.to] == - 1 and e.cap > e.flow) {
					level[e.to] = level[v] + 1;
					q.push(e.to);
				}
			}
		}
		return (level[t] !=  -1);
	};

	auto dfs = [&](int v, int t, W f) {
		auto func = [&](int v, int t, W f, auto func)->W{

			if (v == t) return f;
			for (int &i = iter[v]; i < graph[v].size(); i++) {
				edge &e = graph[v][i];

				if (e.cap > e.flow and level[v] < level[e.to]) {

					W d = func(e.to, t, min(f, e.cap - e.flow), func);

					if (d > 0) {
						e.flow += d, graph[e.to][e.rev].flow -= d;
						return d;
					}
				}
			}

			return 0;
		};
		return func(v, t, f, func);
	};

	while (bfs(s, t)) {
		fill(begin(iter), end(iter), 0);
		while (dfs(s, t, inf) != 0 );
	}

	W ret = 0;
	for (auto &e : graph[s]) ret += e.flow;
	return ret;
}


int main(void) {
	init();

	int n;
	cin >> n;

	rep(i, n) {
		int in;
		cin >> in;
		x[in] = 1;
	}

	rep(i, limit - 1) x[i] ^= x[i + 1];

	vector<int> number[2];
	rep(i, limit) if (x[i]) number[i & 1].push_back(i);

	const int s = number[0].size() + number[1].size();
	const int t = s + 1;
	const int all = t + 1;

	G graph(all);

	rep(i, number[0].size()) add_edge(graph, s, i, 1);
	rep(i, number[0].size()) {
		rep(j, number[1].size()) {
			const int diff = abs(number[1][j] - number[0][i]);
			if (isprime[diff]) add_edge(graph, i, number[0].size() + j, 1);
		}
	}
	rep(j, number[1].size()) add_edge(graph, number[0].size() + j, t, 1);

	const int res = dinic(graph,s,t);

	int ans = res;
	ans += (number[0].size() + number[1].size() - res);
	if( (number[0].size() - res) % 2 == 1) ans++;
	cout << ans << endl;

	return 0;
}

