queue<int>Q;
int prev[__maxNodes];
int matching[__maxNodes]; /*  */
int check[__maxNodes];
int Hungarian()
{
    int ans=0;
    num_edges=edges.size();
    memset(matching,-1,sizeof(matching));
    memset(check,-1,sizeof(check));
    for(int i=0;i<num_left;i++)
    {
        if(matching[i]==-1)
        {
            while(!Q.empty())Q.pop();
            Q.push(i);
            prev[i]=-1;//i
            bool flag=false;//
            while(!Q.empty()&&!flag)
            {
                int u=Q.front();
                for(iterator_t ix=G[u].begin();ix!=G[u].end()&&!flag;++ix)
                {
                    int v=edges[*ix].to;
                    if(check[v]!=i)
                    {
                        check[v]=i;
                        Q.push(matching[v]);
                        if(matching[v]>=0)//
                        {
                            prev[matching[v]]=u;
                        }
                        else//
                        {
                            flag=true;
                            int d=u,e=v;
                            while(d!=-1)
                            {
                                int t=matching[d];
                                matching[t]=e;
                                matching[e]=d;
                                d=prev[d];
                                e=t;
                            }
                        }
                    }
                }
                Q.pop();
            }
            if(matching[i]!=-1)
                ++ans;
        }
    }
    return ans;
}
