bool bp(int x)
{
	for(int i=0;i<l;i++)
	{
		if(!ok[x][i])continue;
		if(vis[i])continue;
		vis[i]=vis[x]=true;
		bool f=false;
		if(match[i]=-1)f=true;
		else
		{
			int xx=match[i];
			match[i]=-1;
			match[xx]=-1;
			f=bp(xx);
			if(!f)match[i]=xx,match[xx]=i;
		} 
		if(f)
		{
			match[i]=x;
			match[x]=i;
			return true;
		}
		vis[i]=vis[x]=false;
	}
	return false;
}
