struct GRAPH
{
	int fst[MX], nxt[MX*MX*2], v[MX*MX*2], w[MX*MX*2], lnum;

	void init()
	{
		memset(fst, 0xff, sizeof(fst));
		lnum = -1;
	}

	void addeg(int nu, int nv, int nw)
	{
		nxt[++lnum] = fst[nu];
		fst[nu] = lnum;
		v[lnum] = nv;
		w[lnum] = nw;
	}

	int vis[MX], dep[MX], que[MX], cur[MX];

	bool bfs(int frm, int tar)
	{
		int h = 1, t = 1;
		memset(dep, 0xff, sizeof(dep));
		que[h] = frm;
		dep[frm] = 0;
		while(h >= t)
		{
			int x = que[t++];
			for(int i=fst[x]; ~i; i=nxt[i])
				if(dep[v[i]]==-1 && w[i])
					dep[v[i]] = dep[x]+1, que[++h] = v[i];
		}
		return (dep[tar]!=-1);
	}

	int dinic(int x, int t, int mn)
	{
		if(x == t) return mn;
		int a, now = 0, y;
		for(int &i=cur[x]; ~i; i=nxt[i])
		{
			y = v[i];
			if(dep[y]==dep[x]+1 && w[i])
			{
				a = dinic(y, t, min(mn-now, w[i]));
				now += a;
				w[i] -= a;
				w[i^1] += a;
				if(now == mn) break;
			}
		}
		return now;
	}

	int mxf(int frm, int tar)
	{
		int f = 0;
		while(bfs(frm, tar))
		{
			memmove(cur, fst, sizeof(cur));
			f += dinic(frm, tar, 1000);
		}
		return f;
	}
} G;

