int dfs(int pos,int imp) {
  if (pos == en || (!imp)) return ans += cur * imp, imp;
  vis[pos] = 1;
  int expo = 0, tmp;
  for (int i = fir[pos] ; i ; i = con[i].la) {
    if ((!con[i].co) && (!vis[con[i].b])) {
      tmp = dfs(con[i].b,min(imp,con[i].cap));
      con[i].cap -= tmp;
      con[i^1].cap += tmp;
      expo += tmp;
      imp -= tmp;
      if (!imp) break;
    }
  }
  return expo;
}
bool spfa() {
  static queue<int> q;
  memset(dis,0x3f,sizeof dis);
  while (!q.empty()) q.pop();
  q.push(st);
  dis[st] = 0;
  inq[st] = 1;
  for (int pos ; !q.empty() ; q.pop()) {
    pos = q.front();
    inq[pos] = 0;
    for (int i = fir[pos] ; i ; i = con[i].la) {
      if (con[i].cap && dis[con[i].b] > dis[pos] + con[i].co) {
	dis[con[i].b] = dis[pos] + con[i].co;
	if (!inq[con[i].b]) {
	  q.push(con[i].b);
	  inq[con[i].b] = 1;
	}
      }
    }
  }
