  for (int pos = 1 ; pos <= n ; ++ pos) {
    vis[pos] = 0;
    for (int i = fir[pos] ; i ; i = con[i].la)
      con[i].co += dis[pos] - dis[con[i].b];
  }
  cur += dis[en];
  return 1;
}
