			if(e[tmp].capa>e[tmp].flow&&!vis[e[tmp].to])
			{
				vis[e[tmp].to]=1;
				d[e[tmp].to]=d[u]+1;
				q.push(e[tmp].to);
			}
		q.pop();
	}
	return vis[t];
}

