void seive() {
	ntpri[1] = 1;
	rep (i, 2, M) {
		if (!ntpri[i]) pri[++cnt] = i;
		rep (j, 1, cnt) {
			if (1LL * i * pri[j] > M) break;
			ntpri[i * pri[j]] = 1;
			if (i % pri[j] == 0) break;
		}
	}
	ntpri[2] = ntpri[0] = 1;
}

int match(int x) {
	vis[x] = 1;
	rep (y, 1, m) if (adj[x][y] && (!link[y] || (!vis[link[y]] && match(link[y])))) {
		link[y] = x;
		return 1;
	}
	return 0;
}

