	b[++m] = a[n];

	seive();

	rep (i, 1, m) if (b[i] & 1) 
		rep (j, 1, m) if (ntpri[abs(b[i] - b[j])] == 0) adj[i][j] = 1; 

	int res = 0, cc = 0;
