    }
    vector<int> val1, val2;
    srep(i,1,MAX_N){
        if(x[i-1] ^ x[i]){
            if(i % 2) val1.pb(i);
            else val2.pb(i);
        }
    }
    const int a = (int)val1.size(), b = (int)val2.size();
    if(a == 0 || b == 0){
        cout << (a + b) / 2 * 2 + (a % 2) << "\n";
        return 0;
    }
    BM bm(a, b);
    rep(i,a){
        rep(j,b){
            if(is_prime[abs(val1[i] - val2[j])]){
                bm.add_edge(i, j);
            }
        }
    }
    const int res = bm.solve();
    cout << res + (a + b - 2 * res) / 2 * 2 + ((a - res) % 2) << "\n";
    return 0;
}

