// binary matching
struct match {
  vi used;
  vvi to;
  vi p;
  int n, m;
  match(int n, int m):used(n),to(n),p(m,-1),n(n),m(m){}
  void add(int a, int b) { to[a].pb(b);}
  bool dfs(int v){
    if(used[v]) return false;
    used[v] = 1;
    rep(i,sz(to[v])) {
      int u = to[v][i];
      if (p[u] == -1 || dfs(p[u])){
        p[u] = v;
        return true;
      }
    }
    return false;
  }
  int solve(){
    int res = 0;
    rep(i,n) {
      rep(j,n) used[j] = 0;
      if(dfs(i)) ++res;
    }
    return res;
  }
};
