inline void add(int u, int v, int w)
{
	e[++tot] = (edge) {v, h[u], w}; h[u] = tot;
	e[++tot] = (edge) {u, h[v], 0}; h[v] = tot;
}

void pre()
{
	static int p[maxp], cnt;
	Vis[1] = 1;
	for (int i = 2; i <= 1e7; ++i) {
		if (!Vis[i]) p[++cnt] = i, Vis[i] = i;
		for (int j = 1; j <= cnt && i * p[j] <= 1e7; ++j) {
			Vis[i * p[j]] = p[j];
			if (p[j] >= Vis[i]) break;
		}
	}
	Vis[2] = 1;
	for (int i = 2; i <= 1e7; ++i)
		if (Vis[i] == i) Vis[i] = 0;
}

bool bfs()
{
	static int u, l, r, q[maxn];
	l = 0; q[r = 1] = s; vis[s] = 1;
	while (l < r) {
		u = q[++l];
		for (int i = h[u], v; v = e[i].to, i; i = e[i].next)
			if (!vis[v] && e[i].cap) {
				vis[v] = 1; dis[v] = dis[u] + 1;
				q[++r] = v;
			}
	}
	return vis[t];
}

int dfs(int u, int a)
{
	if (u == t || !a) return a;
	int f = 0, flow = 0;
	for (int &i = cur[u], v; v = e[i].to, i; i = e[i].next)
		if (dis[v] == dis[u] + 1 && (f = dfs(v, min(e[i].cap, a)))) {
			e[i].cap -= f; e[i ^ 1].cap += f;
			flow += f; a -= f;
			if (!a) break;
		}
	return flow;
}

int dinic()
{
	int res = 0;
	while (bfs()) {
		memset(vis + 1, 0, sizeof(bool) * t);
		memcpy(cur + 1, h + 1, sizeof(int) * t);
		res += dfs(s, 1e9);
	}
	return res;
}

