int N, V;
int A[300];
map<int, int> mp;

bool is_prime(int x) {
  if (x == 1) return false;
  for (int i=2; i<=sqrt(x); i++) {
    if (x % i == 0) return false;
  }
  return true;
}

int f(int x) {
  if (x % 2 == 0) return 2;
  if (is_prime(x)) return 1;
  return 3;
}
signed main() {
  ios::sync_with_stdio(false); cin.tie(0);
  cin >> N;
  rep(i, N) {
    int x;
    cin >> x;
    mp[x]^=1;
    mp[x+1]^=1;
  }
  for (auto p : mp) {
    if (p._2 == 0) continue;
    A[V++] = p._1;
  }
  sort(A, A+V);
  assert(V <= 200);

  using Edge = MaximumWeightedMatching<int>::Edge;
  vector<Edge> edges;
  int C = 4;
  rep(i, V) {
    rep(j, i) {
      int cost = C-f(abs(A[i]-A[j]));
      edges.pb({ i+1, j+1, cost });
      edges.pb({ j+1, i+1, cost });
    }
  }
  auto x = MaximumWeightedMatching<int>(V, edges);
  cout << C*(V/2) - x.maximum_weighted_matching() << "\n";
  return 0;
}

#include <bits/stdc++.h>
#define fi first
#define se second
using namespace std;

const int maxn = 200, maxm = 1e7;
int n, m, k, p[maxm / 10 + 3], a[maxn + 3], mat[maxn + 3];
map<int, bool> M;
bool b[maxm + 3], vis[maxn + 3];
vector<int> G[maxn + 3];

void prework(int n) {
	for (int i = 2; i <= n; i++) {
		if (!b[i]) p[++k] = i;
		for (int j = 1; j <= k && i * p[j] <= n; j++) {
			b[i * p[j]] = true;
			if (i % p[j] == 0) break;
		}
	}
	b[1] = b[2] = true;
}

bool dfs(int u) {
	for (int i = 0, v; i < G[u].size(); i++) {
		if (!vis[v = G[u][i]]) {
			vis[v] = true;
			if (!mat[v] || dfs(mat[v])) {
				mat[v] = u, mat[u] = v;
				return true;
			}
		}
	}
	return false;
}

int main() {
	prework(maxm);
	scanf("%d", &n);
	for (int i = 1, x; i <= n; i++) {
		scanf("%d", &x);
		M[x] ^= 1, M[x + 1] ^= 1;
	}
	for (map<int, bool>::iterator it = M.begin(); it != M.end(); it++) {
		if (it -> se) a[++m] = it -> fi;
	}
	for (int i = 1; i <= m; i++) {
		for (int j = i + 1; j <= m; j++) {
			if (!b[a[j] - a[i]]) {
				G[i].push_back(j), G[j].push_back(i);
			}
		}
	}
	int ans = 0;
	for (int i = 1; i <= m; i++) if (a[i] % 2 && !mat[i]) {
		fill(vis + 1, vis + m + 1, false), ans += dfs(i);
	}
	int odd = 0, even = 0;
	for (int i = 1; i <= m; i++) if (!mat[i]) {
		if (a[i] % 2) {
			if (odd) odd = 0, ans += 2;
			else odd = 1;
		} else {
			if (even) even = 0, ans += 2;
			else even = 1;
		}
	}
	if (odd && even) ans += 3;
	printf("%d\n", ans);
	return 0;
}

