	while(ch<'0'||ch>'9'){if(ch=='-') w=-1;ch=getchar();}
	while(ch>='0'&&ch<='9'){x=(x<<3)+(x<<1)+(ch^48);ch=getchar();}
	return x*w;
}
int to[M*M],nt[M*M],c[M*M],hd[M],tot=1;
void add(int x,int y,int z)
{
	++tot,to[tot]=y,nt[tot]=hd[x],c[tot]=z,hd[x]=tot;
	++tot,to[tot]=x,nt[tot]=hd[y],c[tot]=0,hd[y]=tot;
}
int ps,pt,nhd[M],lv[M];
bool bfs()
{
	queue<int> q; 
	for(int i=1;i<=pt;++i) lv[i]=0;
	lv[ps]=1,q.push(ps);
	while(!q.empty())
	{
		int x=q.front();
		q.pop();
		for(int i=hd[x];i;i=nt[i])
		{
			int y=to[i];
			if(c[i]>0&&!lv[y])
				lv[y]=lv[x]+1,q.push(y);
		}
	}
	return lv[pt];
}
int dfs(int x,int fw)
{
	if(x==pt) return fw;
	int an=0;
	for(int &i=nhd[x];i;i=nt[i])
	{
		int y=to[i];
		if(c[i]>0&&lv[y]==lv[x]+1)
		{
			int dt=dfs(y,min(fw,c[i]));
			c[i]-=dt,c[i^1]+=dt;
			fw-=dt,an+=dt;
			if(!fw) break;
		}
	}
	return an;
}
