const int N = 233;
const int INF = 1e9;

struct Edge {
    int to, rev, cap;
};

int nv, sv, tv;
vector<Edge> g[N];
int dist[N], ptr[N];

void add_edge(int a, int b, int c) {
    g[a].push_back(Edge{b, int(g[b].size()), c});
    g[b].push_back(Edge{a, int(g[a].size()) - 1, 0});
    assert(max(a, b) < nv);
}

bool augment() {
    fill_n(dist, nv, -1);
    dist[sv] = 0;

    vector<int> que;
    que.push_back(sv);
    for (int v: que) {
        for (auto e: g[v]) {
            if (dist[e.to] != -1 || e.cap == 0) {
                continue;
            }
            dist[e.to] = dist[v] + 1;
            if (e.to == tv) {
                return true;
            }
            que.push_back(e.to);
        }
    }

    return false;
}

int dfs(int v, int r) {
    for (int& i = ptr[v]; i < int(g[v].size()); i++) {
        auto& e = g[v][i];
        if (dist[e.to] <= dist[v] || e.cap == 0) {
            continue;
        }
        int d = dfs(e.to, min(r, e.cap));
        if (d == 0) {
            continue;
        }
        e.cap -= d;
        g[e.to][e.rev].cap += d;
        return d;
    }
    return 0;
}

int exec() {
    int res = 0;
    while (augment()) {
        fill_n(ptr, nv, 0);
        while (true) {
            int d = dfs(sv, INF);
            if (d == 0) {
                break;
            }
            res += d;
        }
    }
    return res;
}

const int M = int(1e7 + 10);

int n;
bool isp[M];

void precalc() {
    fill(isp + 2, isp + M, true);
    for (int i = 2; i * i < M; i++) {
        if (!isp[i]) {
            continue;
        }
        for (int j = i * i; j < M; j += i) {
            isp[j] = false;
        }
    }
}

int main() {
    precalc();

    scanf("%d", &n);

    set<int> a;
    auto add = [&](int x) {
        auto it = a.find(x);
        if (it != a.end()) {
            a.erase(it);
        } else {
            a.insert(x);
        }
    };

    for (int i = 0; i < n; i++) {
        int x;
        scanf("%d", &x);
        add(x);
        add(x + 1);
    }

    vector<vector<int>> v(2);
    for (int x: a) {
        v[x % 2].push_back(x);
    }
    int v0s = v[0].size(), v1s = v[1].size();

    sv = 0;
    tv = 1;
    nv = v0s + v1s + 2;
    for (int i = 0; i < v0s; i++) {
        add_edge(sv, 2 + i, 1);
    }
    for (int i = 0; i < v1s; i++) {
        add_edge(2 + v0s + i, tv, 1);
    }
    for (int i = 0; i < v0s; i++) {
        for (int j = 0; j < v1s; j++) {
            int d = abs(v[0][i] - v[1][j]);
            if (d > 2 && isp[d]) {
                add_edge(2 + i, 2 + v0s + j, 1);
            }
        }
    }

    int used = exec();
    int ans = used;
    ans += 2 * ((v0s - used) / 2);
    ans += 2 * ((v1s - used) / 2);
    if ((v0s - used) % 2 == 1) {
        ans += 3;
    }

    printf("%d\n", ans);
}

