		if(a[i] != a[i - 1] + 1) b[++cnt] = a[i];
		if(a[i] != a[i + 1] - 1) b[++cnt] = a[i] + 1;
	}
	for(int i = 1; i <= cnt; ++i){
		if(b[i] & 1) x[++numx] = b[i];
		else y[++numy] = b[i];
	}
