			if(!vis[abs(x[i] - y[j])])
				add(i, j);
	for(int i = 1; i <= numx; ++i){
		memset(book, false, sizeof book);
		dfs(i);
	}
	int ans = 0;
	for(int i = 1; i <= numy; ++i)
		if(link[i]) 
			++ans;
	numy -= ans;numx -= ans;
