}

void add(int u,int v){
	e[++cnt].to = v;e[cnt].nxt = fir[u];fir[u] = cnt;
	return ;
}

void init(){
	int m = 1e7;
	for(int i = 2; i <= m; ++i){
		if(!vis[i]) prime[++tot] = i;
		for(int j = 1; j <= tot && i * prime[j] <= m; ++j){
			vis[i * prime[j]] = true;
			if(i % prime[j] == 0) break;
		}
	}
	vis[1] = vis[2] = true;
	return ;
}

