#define MAX_N 207
#define MAX_P 10000007

int N, res;
int a[MAX_N], b[MAX_N];
int pri[MAX_P / 10], tot;
int match[MAX_N];
bool check[MAX_P], con[MAX_N][MAX_N], vis[MAX_N];

void sieve(int n) {
    rep(i, 2, n) {
        if (!check[i]) pri[++tot] = i;
        Rep(j, tot) {
            if (i * pri[j] > n) break;
            check[i * pri[j]] = true;
            if (i % pri[j] == 0) break;
        }
    }
    check[2] = check[1] = 1;
}

bool dfs(int x) {
    Rep(j, N) if (con[x][j]) {
        if (!vis[j] && (!match[j] || dfs(match[j]))) {
            vis[j] = true;
            match[j] = x;
            res++;
            return true;
        }
    }
    return false;
}

void solve() {
    int top = 0;
    std::sort(a + 1, a + N + 1);
    b[top = 1] = a[1];
    a[N + 1] = 0;
    Rep(i, N) {
        if (b[top] != a[i] && a[i - 1] != a[i] - 1) b[++top] = a[i];
        if (a[i] + 1 != a[i + 1]) b[++top] = a[i] + 1;
    }
    N = top;
    Rep(i, N) a[i] = b[i];
    // Rep(i, N) printf("%d ", a[i]); puts("");return;
    Rep(i, N) if (a[i] & 1) {
        Rep(j, N) if (a[j] % 2 == 0 && !check[std::abs(a[i] - a[j])]) con[i][j] = true;
    }
    Rep(i, N) if (a[i] & 1) {
        Rep(j, N) vis[j] = false;
        dfs(i);
    }
    Rep(i, N) vis[i] = false;
    Rep(i, N) if (a[i] % 2 == 0 && match[i]) vis[i] = vis[match[i]] = true;
    int cnt[2] = {0};
    Rep(i, N) if (!vis[i]) cnt[a[i] & 1]++;
    res += 2 * (cnt[0] / 2 + cnt[1] / 2);
    if (cnt[0] & 1) res += 3;
    printf("%d\n", res);
}

int main() {
    read(N);
    int mx = 0;
    Rep(i, N) read(a[i]), chmax(mx, a[i]);
    sieve(mx);
    solve();
    return 0;
}

