const int maxn = 200, maxm = 1e7 + 1;
int n, m, k, p[maxm / 10 + 3], a[maxn + 3], mat[maxn + 3];
map<int, bool> M;
bool b[maxm + 3], vis[maxn + 3];
vector<int> G[maxn + 3];

void prework(int n) {
	for (int i = 2; i <= n; i++) {
		if (!b[i]) p[++k] = i;
		for (int j = 1; j <= k && i * p[j] <= n; j++) {
			b[i * p[j]] = true;
			if (i % p[j] == 0) break;
		}
	}
}

bool dfs(int u) {
	for (int i = 0, v; i < G[u].size(); i++) {
		if (!vis[v = G[u][i]]) {
			vis[v] = true;
			if (!mat[v] || dfs(v)) {
				mat[v] = u, mat[u] = v;
				return true;
			}
		}
	}
	return false;
}

int main() {
	prework(maxm);
	scanf("%d", &n);
	for (int i = 1, x; i <= n; i++) {
		scanf("%d", &x);
		M[x] ^= 1, M[x + 1] ^= 1;
	}
	for (map<int, bool>::iterator it = M.begin(); it != M.end(); it++) {
		if (it -> se) a[++m] = it -> fi;
	}
	for (int i = 1; i <= m; i++) {
		for (int j = i + 1; j <= m; j++) {
			if (p[lower_bound(p + 1, p + k + 1, a[j] - a[i]) - p] == a[j] - a[i]) {
				G[i].push_back(j), G[j].push_back(i);
			}
		}
	}
	int ans = 0;
	for (int i = 1; i <= m; i++) if (a[i] % 2 && !mat[i]) {
		fill(vis + 1, vis + m + 1, false), ans += dfs(i);
	}
	int odd = 0, even = 0;
	for (int i = 1; i <= m; i++) if (!mat[i]) {
		if (a[i] % 2) {
			if (odd) odd = 0, ans += 2;
			else odd = 1;
		} else {
			if (even) even = 0, ans += 2;
			else even = 1;
		}
	}
	if (odd && even) ans += 3;
	printf("%d\n", ans);
	return 0;
}

