			}
		}
	}
	use[0] = use[1] = use[2] = true;
	vector<vector<int> > g(v[0].size());
	for (int j = 0; j < v[0].size(); j++){
		for (int k = 0; k < v[1].size(); k++){
			if (!use[abs(v[1][k] - v[0][j])]){
				g[j].push_back(k);
			}
		}
	}
	int num = bipartiteMatching(g);
	int f = ((v[0].size() - num) / 2)*2;
	f += num;
	f += ((v[1].size() - num) / 2) * 2;
	if ((v[0].size()-num) & 1){
		if ((v[1].size() - num) % 2 == 0){
			return 1;
		}
		f += 3;
	}
	else{
		if ((v[1].size() - num) & 1){
			return 1;
		}
	}
	cout << f << endl;
	return 0;
}

