int get(int x)
{
    if (x == 0)
    {
        return 0;
    }
    if (x == 2)
    {
        return 3;
    }
    if (x > 2 && prime[x])
    {
        return 1;
    }
    else
    {
        return 2 + (x % 2);
    }
}

