    for (int i = 2; i < M; i++)
    {
        if (prime[i])
        {
            for (int j = i + i; j < M; j += i)
            {
                prime[j] = 0;
            }
        }
    }
    int n;
    scanf("%d", &n);
    vector <int> x(n);
    for (int i = 0; i < n; i++)
    {
        for (int j = 0; j < n; j++)
        {
            dp[i][j][0] = 1e18;
            dp[i][j][1] = 1e18;
        }
    }
    for (int i = 0; i < n; i++)
    {
        scanf("%d", &x[i]);
        dp[i][i][0] = 0;
    }
    for (int len = 1; len <= n; len++)
    {
        for (int l = 0; l + len - 1 < n; l++)
        {
            int r = l + len - 1;
            for (int t = 0; t < 2; t++)
            {
                for (int j = l; j < r; j++)
                {
                    for (int k = j + 1; k < r; k++)
                    {
                        ll cost = 0;
                        cost += dp[l][j][t];
                        cost += dp[j + 1][k][t];
                        cost += dp[k + 1][r][t];
                        if (t == 0)
                        {
                            cost += get(x[j + 1] - x[j] - 1);
                            cost += get(x[k + 1] - x[k] - 1);
                        }
                        dp[l][r][t] = min(dp[l][r][t], cost);
                    }
                    dp[l][r][t] = min(dp[l][r][t], dp[l][j][t] + (t == 0 ? get(x[j + 1] - x[j] - 1) : 0) + dp[j + 1][r][t]);
                }
            }
            for (int t = 0; t < 2; t++)
            {
                dp[l][r][t] = min(dp[l][r][t], dp[l][r][t ^ 1] + get(x[r] - x[l] + 1));
            }
        }
    }
    printf("%lld\n", dp[0][n - 1][1]);
}

