template <class T> using reversed_priority_queue = priority_queue<T, vector<T>, greater<T> >;
typedef long long ll;

template <class T> struct FordFulkerson {

    vector < pair < int,int > > maching;

    struct  Edge {
        int to,rev; T cap;
        Edge(int t, int r, T c) : to(t), rev(r), cap(c) {}
    };

    const T INF = numeric_limits<T>::max() / 2;
    vector< vector< Edge > > G;
    vector< bool > used;
    FordFulkerson (int n) : G(n+1), used(n+1) {}

    void add_edge(int from, int to, T cap) {
        G[from].push_back(Edge(to,(int)G[to].size(),cap));
        G[to].push_back(Edge(from,(int)G[from].size()-1,0));
    }

    T dfs(int v, int t, T f) {
        if (v == t) return f;
        used[v] = true;
        for (int i = 0; i < G[v].size(); i++) {
            Edge &e = G[v][i];
            if (!used[e.to] && e.cap > 0) {
                T d = dfs(e.to, t, min(f,e.cap));
                if (d > 0) {
                    if (v != 290 && e.to != 291) {
                        maching.push_back(make_pair(v, e.to - 150));
                    }
                    e.cap -= d;
                    G[e.to][e.rev].cap += d;
                    return d;
                }
            }
        }
        return 0;
    }

    T get_max (int source, int sink) {
        T flow = 0;
        while (true) {
            for (int i = 0; i < used.size(); i++) used[i] = false;
            T f = dfs(source, sink, INF);
            if (f == 0) return flow;
            flow += f;
        }
    }

};


bool isPrime(long long n) {
    if (n <= 2) return false;
    for (long long i = 2; i * i <= n; i++) if (n % i == 0) return false;
    return true;
}

bool flip[10000010];

int main ()
{
    int N;
    cin >> N;
    vector < int > x(N);
    REP(i,N) cin >> x[i];

    REP(i,N) {
        flip[x[i]] = !flip[x[i]];
        flip[x[i] + 1] = !flip[x[i] + 1];
    }

    vector < int > xt;
    for (int i = 0; i < 10000010; i++) {
        if (flip[i]) xt.push_back(i);
    }

    FordFulkerson < int > inst(300);
    for (int i = 0; i < xt.size(); i++) {
        for (int j = i; j < xt.size(); j++) {
            if (i == j) continue;
            int diff = abs(xt[i] - xt[j]);
            if (isPrime(diff)) {
                inst.add_edge(i, j + 150, 1);
            }
        }
    }
    REP(i,N) {
        inst.add_edge(290, i, 1);
        inst.add_edge(i + 150, 291, 1);
    }

    int maxt = inst.get_max (290, 291);
    vector < pair < int,int > > mc = inst.maching;

    REP(i,mc.size()) {
        xt[mc[i].first]  = -1;
        xt[mc[i].second] = -1;
    }

    int ans = maxt;
    REP(i,xt.size()) {
        REP(j,xt.size()) {
            if (i == j) continue;
            if (xt[i] == -1) continue;
            if (xt[j] == -1) continue;
            if (abs(xt[i] - xt[j]) % 2 == 0 && abs(xt[i] - xt[j]) != 2) {
                xt[i] = -1;
                xt[j] = -1;
                ans += 2;
            }
        }
    }

    int sum = 0;
    REP(i,xt.size()) {
        if (xt[i] != -1) {
            sum += 3;
        }
    }

    ans += sum / 2;
    cout << ans << endl;

    return 0;
}

