inline void Init() {
    for(ri i = 2; i <= N + 1; i ++) {
        if(!nop[i]) pr[++ tot] = i;
        for(ri j = 1; j <= tot; j ++) {
            int nx = i * pr[j]; if(nx > N + 1) break;
            nop[nx] = 1; if(i % pr[j] == 0) break;
        }
    }
    nop[1] = nop[2] = 1;
}

inline int dfs(int o) {
    for(int i = 1; i <= no; i ++)
    if(ex[o][i] && vis[i] != tim) {
        vis[i] = tim;
        if(!mat[i] || dfs(mat[i])) 
        return mat[i] = o, 1;
    }
    return 0;
}

