    nx[++tot] = fir[_];
    fir[_] = tot;
    to[tot] = __;
}

bool dfs(int x) {
    if (vis[x]) return false;
    vis[x] = 1;
    for (int i = fir[x]; i; i = nx[i]) {
        int toi = to[i];
        if (!match[toi] || dfs(match[toi])) {
            match[toi] = x;
            return true;
        }
    }
    return false;
}

