    rep (i, 1, cnt[1]) rep (j, 1, cnt[2]) if (!notprime[abs(odd[i] - even[j])])  add(i, j);
    int res = 0;
    rep (i, 1, cnt[1]) {
        memset(vis, 0, sizeof vis);
        if (dfs(i)) ++res;
    }
    int ans = res; cnt[1] -= res, cnt[2] -= res;
    ans += cnt[1] + cnt[2] + (cnt[1] & 1);
    printf("%d\n", ans);
