inline int GetTms(int xx)
{
	if (xx == 1)
	{
		return 3;
	}
	if (!(xx & 1))
	{
		return 2;
	}
	else if (!chk[xx])
	{
		return 1;
	}
	else
	{
		return 3;
	}
}

