		{
			prm[tot++] = i;
		}
		for (int j = 0;j < tot;j++)
		{
			if ((long long)i*(long long)prm[j] > (long long)(T - 3))
			{
				break;
			}
			chk[i*prm[j]] = true;
			if (i%prm[j] == 0)
