		{
			cp[cnt].second = i - 1;
			cp[++cnt].first = i;
		}
	}
	cp[cnt].second = n - 1;
	cnt++;
	for (int i = 0;i < cnt;i++)
	{
		for (int j = i;j < cnt;j++)
		{
			len1[i][j] = a[cp[j].second] - a[cp[i].first] + 1;
		}
	}
	for (int i = 0;i < cnt - 1;i++)
	{
		for (int j = i;j < cnt - 1;j++)
		{
			len2[i][j] = a[cp[j + 1].first] - a[cp[i].second] - 1;
		}
	}
	for (int len = 1;len <= cnt;len++)
	{
		for (int i = 0;i < cnt;i++)
		{
			int j = i + len - 1;
			if (j >= cnt)
			{
				break;
			}
			if (len == 1)
			{
				dp1[i][j] = GetTms(GetLen1(i, j));
			}
			else
			{
				dp1[i][j] = GetTms(GetLen1(i, j)) + dp2[i][j - 1];
			}
			for (int kk = i;kk < j;kk++)
			{
				dp1[i][j] = min(dp1[i][j], dp1[i][kk] + dp1[kk + 1][j]);
			}
		}
		for (int i = 0;i < cnt - 1;i++)
		{
			int j = i + len - 1;
			if (j >= cnt - 1)
			{
				break;
			}
			if (len == 1)
			{
				dp2[i][j] = GetTms(GetLen2(i, j));
			}
			else
			{
				dp2[i][j] = GetTms(GetLen2(i, j)) + dp1[i + 1][j];
			}
			for (int kk = i;kk < j;kk++)
			{
				dp2[i][j] = min(dp2[i][j], dp2[i][kk] + dp2[kk + 1][j]);
			}
		}
	}
	printf("%d\n", dp1[0][cnt - 1]);
	//system("pause");
	return 0;
}

