using namespace std;

struct edge {
	int to, cap, rev;
};

vector<edge>X[1009]; bool used[1009];

void add_edge(int u, int v, int w) {
	X[u].push_back(edge{ v,w,(int)X[v].size() });
	X[v].push_back(edge{ u,0,(int)X[u].size() - 1 });
}
int dfs(int pos, int to, int flow) {
	if (pos == to) return flow;
	used[pos] = true;
	for (int i = 0; i < X[pos].size(); i++) {
		if (used[X[pos][i].to] == true || X[pos][i].cap <= 0) continue;
		int G = dfs(X[pos][i].to, to, min(X[pos][i].cap, flow));
		if (G == 0) continue;
		X[pos][i].cap -= G;
		X[X[pos][i].to][X[pos][i].rev].cap += G;
		return G;
	}
	return 0;
}
int max_flow(int s, int t) {
	int F = 0;
	while (true) {
		for (int i = 0; i <= 500; i++) used[i] = false;
		int fl = dfs(s, t, 1000000007);
		if (fl == 0) break;
		F += fl;
	}
	return F;
}

int n, x[109], g[10000009]; bool prime[10000009]; vector<int>J;

int main() {
	for (int i = 2; i <= 10000008; i++) prime[i] = true;
	for (int i = 2; i*i <= 10000008; i++) {
		for (int j = i*i; j <= 10000008; j += i) prime[j] = false;
	}
	cin >> n;
	for (int i = 1; i <= n; i++) { cin >> x[i]; g[x[i]] ^= 1; g[x[i] + 1] ^= 1; }
	for (int i = 1; i <= 10000002; i++) { if (g[i] >= 1) J.push_back(i); }
	for (int i = 0; i < J.size(); i++) {
		for (int j = i + 1; j < J.size(); j++) {
			if (prime[J[j] - J[i]] == true) {
				if (J[i] % 2 == 1) add_edge(i, j, 1);
				else add_edge(j, i, 1);
			}
		}
	}
	int kisuu = 0, gusuu = 0;
	for (int i = 0; i < J.size(); i++) {
		if (J[i] % 2 == 1) {
			add_edge(J.size(), i, 1);
			kisuu++;
		}
		else {
			add_edge(i, J.size() + 1, 1);
			gusuu++;
		}
	}
	int res = max_flow(J.size(), J.size() + 1);
	int V1 = res, V2 = (kisuu - res) / 2 + (gusuu - res) / 2, V3 = (J.size() / 2 - V1 - V2);
	cout << V1 * 1 + V2 * 2 + V3 * 3 << endl;
	return 0;
}

