template <typename T> inline void read(T &AKNOI) {
	T x = 0, flag = 1;
	char ch = getchar();
	while (!isdigit(ch)) {
		if (ch == '-') flag = -1;
		ch = getchar();
	}
	while (isdigit(ch)) {
		x = x * 10 + ch - '0';
		ch = getchar();
	}
	AKNOI = flag * x;
}

int n, a[N], pos[N], tot;
int pr[MAXP], pcnt, notp[MAXP];
int g[N][N], my[N], vis[N], tim;

void sieve(int m) {
	for (int i = 2; i <= m; ++i) {
		if (!notp[i]) {
			pr[++pcnt] = i;
		}
		for (int j = 1; j <= pcnt && i * pr[j] <= m; ++j) {
			notp[i * pr[j]] = 1;
			if (i % pr[j] == 0) break;
		}
	}
}

int Match(int u) {
	for (int v = 1; v <= n; ++v) {
		if (g[u][v] && vis[v] != tim) {
			vis[v] = tim;
			if (!my[v] || Match(my[v])) {
				my[v] = u;
				return 1;
			}
		}
	}
	return 0;
}

void init() {
	read(n);
	for (int i = 1; i <= n; ++i) {
		read(a[i]);
		if (i == 1 || a[i - 1] != a[i] - 1) pos[++tot] = a[i];
		if (i != 1 && a[i - 1] + 1 != a[i]) pos[++tot] = a[i - 1] + 1;
	}
	pos[++tot] = a[n] + 1;
	sieve(a[n] + 1);
}

void solve() {
	int odd = 0;
	for (int i = 1; i <= tot; ++i) {
		if (pos[i] & 1) {
			++odd;
			for (int j = 1; j <= tot; ++j) {
				if (pos[j] & 1) continue;
				int dis = abs(pos[i] - pos[j]);
				if (dis > 2 && !notp[dis]) {
					g[i][j] = 1;
				}
			}
		}
	}
	int ans = 0;
	for (int i = 1; i <= tot; ++i) {
		if (pos[i] & 1) {
			++tim;
			ans += Match(i);
		}
	}
	printf("%d\n", ans + (tot - ans * 2) + ((odd - ans) & 1));
}

int main() {
	init();
	solve();
	return 0;
}

