using namespace std;

int N,M;
const int MAX_N = 111;
int a[MAX_N],b[MAX_N*2],sz;

vector<int> path[MAX_N*4];
bool visit[MAX_N*2];
int match[MAX_N*4];

bool dfs(int a){
	if(visit[a])return false;
	visit[a]=1;
	for(int i=0;i<path[a].size();i++){
		int b=path[a][i];
		if(match[b]<0){
			match[b]=a;
			return true;
		}else if(dfs(match[b])){
			return true;
		}
	}
	return false;
}

bool isPrime(int x){
	if(x==1)return false;
	if(x%2==0)return false; // 2 is exception
	for(int i=3;i*i<=x;i+=2){
		if(x%i==0)return false;
	}
	return true;
}

int main(){

	scanf("%d",&N);
	for(int i=1;i<=N;i++){
		scanf("%d",&a[i]);
	}
	a[0]=-1;
	a[N+1]=1e9;

	int even=0,odd=0;
	for(int i=1;i<=N+1;i++){
		if(a[i-1]+1<a[i]){
			if(i>1)b[sz++]=a[i-1]+1;
			if(i<=N)b[sz++]=a[i];
		}
	}

	for(int i=0;i<sz;i++){
		if(b[i]%2==0)even++;
		else odd++;
	}

	for(int i=0;i<sz;i++){
		for(int j=i+1;j<sz;j++){
			if(isPrime(b[j]-b[i])){
				path[i].push_back(j+sz);
				path[j+sz].push_back(i);
			}
		}
	}

	for(int i=0;i<sz*2;i++){
		match[i]=-1;
	}

	int cnt=0;
	for(int i=0;i<sz;i++){
		for(int j=0;j<sz;j++)visit[j]=0;
		if(dfs(i))cnt++;
	}
	even-=cnt;
	odd-=cnt;
	printf("%d\n",cnt+(even/2+odd/2)*2+(even%2)*3);

	return 0;
}

